    /**
     * deploy initial smart contract
     * @return void
     */
    public function deployContract($contractInfo, $compiledConract): void
    {

        $userAddress = $contractInfo->userAddress;

        $web3 = new Web3(new HttpProvider('https://polygon-amoy.g.alchemy.com/v2/LuZ5CnAEURDtdQRwm9VJlkHRQR29Kw_a'));
        //$eth = $web3->eth;

        $contract = new Contract('https://sepolia.infura.io/v3/6ced7b98fae94ba18b14324cf1331fd0', $compiledConract['abi']);

        function bcdechex($dec) {
            $hex = '';
            do {
                $last = bcmod($dec, 16);
                $hex = dechex($last).$hex;
                $dec = bcdiv(bcsub($dec, $last), 16);
            } while($dec>0);

            return $hex;
        }

        $contract->bytecode('0x' . $compiledConract->object)->new([
            'from' => $userAddress,
            'gas' => '0x' . bcdechex('3000000'),
            'value'=> '0x' . bcdechex('12000000000000000000')
        ], function ($err, $result){
            if ($err) {
                echo "\nerror is:" . $err . "\n";
            }
            if ($result) {
                echo "\nTransaction has made:) id: " . $result . "\n";
            }
        });

